import './App.css';
import React, { useState } from "react";
import { Unity, useUnityContext } from "react-unity-webgl";

function App() {
  const [showUnity, setShowUnity] = useState(false);

  const { unityProvider } = useUnityContext({
    loaderUrl: "Build/MergePuzzle.loader.js",
    dataUrl: "Build/MergePuzzle.data",
    frameworkUrl: "Build/MergePuzzle.framework.js",
    codeUrl: "Build/MergePuzzle.wasm",
  });

  return (
    <div className="App" style={{ color: "#fff", background: "#222", minHeight: "100vh" }}>
      <header className="App-header" style={{ padding: "2rem" }}>
        <h1>ポートフォリオ</h1>
        <section style={{ marginBottom: "2rem" }}>
          <h2>プロフィール</h2>
          <p>
            名前：林田 歴<br />
            ソフトウェアエンジニア。ReactやUnityを使った開発が得意です。<br />
            趣味はゲーム制作とWebアプリ開発です。
          </p>
        </section>
        <section>
          <h2>成果物</h2>
          <button
            onClick={() => setShowUnity(true)}
            style={{
              padding: "0.8em 2em",
              fontSize: "1.1em",
              borderRadius: "8px",
              border: "none",
              background: "#4caf50",
              color: "#fff",
              cursor: "pointer",
              marginBottom: "1.5rem"
            }}
          >
            Unityゲームを表示
          </button>
          {showUnity && (
            <div style={{ marginTop: "1.5rem" }}>
              <Unity
                unityProvider={unityProvider}
                style={{
                  width: 800,
                  height: 600,
                  border: "2px solid #222",
                  background: "#222",
                }}
              />
            </div>
          )}
        </section>
      </header>
    </div>
  );
}

export default App;
